{
 "services": [
   {
     "apikey":      "TEF",               // Chave de acesso para o serviço (API Key)
     "cbroker":     "http://{{url}}:1026", // URL do Orion Context Broker associado ao serviço
     "entity_type": "Sensor",             // Tipo de entidade associado ao serviço
     "resource":    ""                   // Recurso associado ao serviço (pode estar vazio)
   }
 ]
}

{
    "devices": [
        {
            "device_id": "sensor001", // Identificador único do dispositivo
            "entity_name": "urn:ngsi-ld:Sensor:001", // Nome da entidade para o dispositivo (formato URN)
            "entity_type": "Sensor", // Tipo da entidade (neste caso, "Lamp")
            "protocol": "PDI-IoTA-UltraLight", // Protocolo usado para a comunicação (no exemplo, PDI-IoTA-UltraLight)
            "transport": "MQTT", // Método de transporte (neste caso, MQTT)

            // Lista de comandos que o dispositivo pode receber
            "commands": [
                { "name": "on", "type": "command" }, // Comando para ligar
                { "name": "off","type": "command" }, // Comando para desligar
                { "name": "unity","type": "command" }, // Comando para trocar a unidade de medida
                { "name": "maxTemp", "type": "command"}, // Comando para trocar a temperatura máxima
                { "name": "minTemp", "type": "command"}, // Comando para trocar a temperatura mínima
                { "name": "sendTimeout","type":"command"} // Comando para trocar o intervalo de envio
            ],

            // Lista de atributos associados ao dispositivo
            "attributes": [
                { "object_id": "s","name": "state", "type": "Text" }, // Atributo de estado (Texto)
                { "object_id":"t","name":"temperature","type":"Double"},
                { "object_id":"u","name":"unity","type":"Text"},
                { "object_id": "maxT", "name": "maximumTemp","type": "Integer" },
                { "object_id": "minT", "name": "minimumTemp","type": "Integer" },
                { "object_id": "st", "name": "sendTimeout","type": "Integer" }
            ]
        }
    ]
}

{
  "description": "Sensor Commands", // Descrição do serviço de notificação
  "dataProvided": {
    "entities": [
      {
        "id": "urn:ngsi-ld:Sensor:001", "type": "Sensor" // Definição da entidade a ser monitorada
      }
    ],
    "attrs": ["on", "off","unity","maxTemp","minTemp","sendTimeout"] // Atributos a serem monitorados (no caso, 'on' e 'off')
  },
  "provider": {
    "http": { "url": "http://{{url}}:4041" }, // URL do serviço a ser notificado
    "legacyForwarding": true // Indicação para encaminhar notificações de forma legada (se aplicável)
  }
}

{
  "description": "Notify STH-Comet of all Motion Sensor count changes", // Descrição da notificação
  "subject": {
    "entities": [
      {
        "id": "urn:ngsi-ld:Sensor:001",
        "type": "Sensor"
      }
    ],
    "condition": { "attrs": ["temperature"] } // Condição para acionar a notificação (alteração de luminosidade)
  },
  "notification": {
    "http": {
      "url": "http://{{url}}:8666/notify" // URL para notificação do STH-Comet
    },
    "attrs": [
      "temperature" // Atributo a ser notificado (luminosidade)
    ],
    "attrsFormat": "legacy" // Formato dos atributos a ser notificado (legado)
  }
}

